import java.io.FileReader;

import org.apache.commons.io.IOUtils;
import org.brownsocks.payments.gateways.enets.pgp.BCPGPProvider;
import org.brownsocks.payments.gateways.enets.pgp.PGPProvider;


public class TestNoti {
	
	private static final String PRIVATE_KEY_FILENAME = "angkor2011-priv.pgp.asc";
	private static final String PUBLIC_KEY_FILENAME = "src/resources/org/brownsocks/payments/gateways/enets/nets-production-pub.pgp.asc";
	private static final String PRIVATE_KEY_PASSPHRASE = "Password1$";

	public static final String message1 = "";
	public static final String message2 = "";
	public static final String message3 = "";
	public static final String message4 = "";
	
	public static void main(String[] args) throws Exception {
		String privateKey = new String(IOUtils.toByteArray(new FileReader(PRIVATE_KEY_FILENAME)));
		String pubKey = new String(IOUtils.toByteArray(new FileReader(PUBLIC_KEY_FILENAME)));
		
		PGPProvider pgpProvider = new BCPGPProvider();
		pgpProvider.initialize(privateKey, PRIVATE_KEY_PASSPHRASE, pubKey);
		
		System.out.println(pgpProvider.decryptAndVerify(message1));
		System.out.println(pgpProvider.decryptAndVerify(message2));
		System.out.println(pgpProvider.decryptAndVerify(message3));
		System.out.println(pgpProvider.decryptAndVerify(message4));
		

	}
	
	
}
